## Oracle CLI v1.0
Location: /home/ubuntu/oracle-cli.py
Symlink: sudo ln -s /home/ubuntu/oracle-cli.py /usr/local/bin/oracle

```python
#!/usr/bin/env python3
"""
Oracle CLI v1.0 - Command Line for Oracle VM

Usage:
    oracle status              - All services health
    oracle restart <service>   - Restart service  
    oracle logs <service> [n]  - Show logs
    oracle run <recipe>        - Run recipe
    oracle recipes             - List recipes
    oracle add "desc"          - Add changelog feature
    oracle pending             - Show pending features
    oracle release [type]      - Release version
    oracle health              - Quick health check
    oracle top                 - Top processes
"""

import sys, json, requests, socket, subprocess

API = "http://localhost:5001"

class C:
    G = '\033[92m'
    R = '\033[91m'
    B = '\033[1m'
    E = '\033[0m'

def ok(m): print(f"{C.G}OK {m}{C.E}")
def err(m): print(f"{C.R}ERR {m}{C.E}")
def inf(m): print(f"INFO {m}")

def get(ep):
    try: return requests.get(f"{API}{ep}", timeout=30).json()
    except: return {"error": "API unavailable"}

def post(ep, data=None):
    try: return requests.post(f"{API}{ep}", json=data or {}, timeout=60).json()
    except: return {"error": "API unavailable"}

def cmd_status():
    r = get("/diagnose/all")
    if "error" in r: err(r["error"]); return
    print(f"\n{C.B}SERVICES{C.E}")
    for s in r.get("services", []):
        i = "+" if s["healthy"] else "-"
        print(f"  [{i}] {s['name']:<25} {s['active']}/{s['sub']}")
    print(f"\n  {r.get('healthy',0)}/{r.get('total',0)} healthy")

def cmd_restart(svc):
    if not svc.startswith("grok-"): svc = f"grok-{svc}"
    inf(f"Restarting {svc}...")
    r = post("/services/restart", {"service": svc})
    ok(f"{svc} restarted") if r.get("success") else err(r.get("error","Failed"))

def cmd_logs(svc, n=50):
    if not svc.startswith("grok-"): svc = f"grok-{svc}"
    r = post("/services/logs", {"service": svc, "lines": int(n)})
    print(r.get("logs", r.get("error", "No logs")))

def cmd_recipes():
    sys.path.insert(0, '/home/ubuntu')
    try:
        from agent_mode import RECIPES
        print(f"\n{C.B}RECIPES{C.E}")
        for n, rc in RECIPES.items():
            print(f"  {n:<25} {rc['name']}")
    except: err("Agent not available")

def cmd_run(recipe):
    sys.path.insert(0, '/home/ubuntu')
    try:
        from agent_mode import agent
        inf(f"Running: {recipe}")
        r = agent.run_recipe(recipe)
        ok("Done") if r.get("success") else err("Failed")
        print(r.get("output", ""))
    except Exception as e: err(str(e))

def cmd_add(desc):
    sys.path.insert(0, '/home/ubuntu')
    try:
        from changelog_agent import changelog
        r = changelog.add_feature(desc)
        if r.get("success"):
            f = r["feature"]
            ok(f"Added: {f['description']}")
            print(f"  Type: {f['change_type']}, Queue: {r['queue_size']}")
        else: err("Failed")
    except Exception as e: err(str(e))

def cmd_pending():
    sys.path.insert(0, '/home/ubuntu')
    try:
        from changelog_agent import changelog
        r = changelog.list_pending()
        if not r.get("pending"): inf("No pending"); return
        print(f"\n{C.B}PENDING ({len(r['pending'])}){C.E}")
        for f in r["pending"]:
            print(f"  [{f['change_type']}] {f['description']}")
    except Exception as e: err(str(e))

def cmd_release(bump=None):
    sys.path.insert(0, '/home/ubuntu')
    try:
        from changelog_agent import changelog
        r = changelog.release(bump)
        if r.get("success"): ok(f"Released v{r['version']} ({r['features_count']} features)")
        else: err(r.get("error", "No features"))
    except Exception as e: err(str(e))

def cmd_version():
    sys.path.insert(0, '/home/ubuntu')
    try:
        from changelog_agent import changelog
        print(f"v{changelog.get_version_string()}")
    except: err("N/A")

def cmd_health():
    print(f"\n{C.B}HEALTH{C.E}")
    r = get("/health")
    print(f"  API: {'UP' if 'error' not in r else 'DOWN'}")
    r = get("/diagnose/all")
    if "error" not in r: print(f"  Services: {r.get('healthy',0)}/{r.get('total',0)}")
    for p,n in [(5000,"Transcriber"),(5001,"API"),(3335,"Portal")]:
        try:
            s = socket.socket(); s.settimeout(1)
            up = s.connect_ex(('localhost',p)) == 0; s.close()
            print(f"  {n}: {'UP' if up else 'DOWN'}")
        except: pass

def cmd_top():
    r = subprocess.run(["ps","aux","--sort=-%cpu"], capture_output=True, text=True)
    for l in r.stdout.split('\n')[:11]: print(l[:80])

def cmd_help():
    print(__doc__)

def main():
    if len(sys.argv) < 2: cmd_help(); return
    cmd, args = sys.argv[1].lower(), sys.argv[2:]
    cmds = {
        "status": cmd_status, 
        "restart": lambda: cmd_restart(args[0]) if args else err("Need svc"),
        "logs": lambda: cmd_logs(args[0], args[1] if len(args)>1 else 50) if args else err("Need svc"),
        "recipes": cmd_recipes, 
        "run": lambda: cmd_run(args[0]) if args else err("Need recipe"),
        "add": lambda: cmd_add(" ".join(args)) if args else err("Need desc"),
        "pending": cmd_pending, 
        "release": lambda: cmd_release(args[0] if args else None),
        "version": cmd_version, 
        "health": cmd_health, 
        "top": cmd_top, 
        "help": cmd_help,
    }
    if cmd in cmds:
        try: cmds[cmd]()
        except Exception as e: err(str(e))
    else: err(f"Unknown: {cmd}")

if __name__ == "__main__":
    main()
```

## Installation
```bash
chmod +x /home/ubuntu/oracle-cli.py
sudo ln -s /home/ubuntu/oracle-cli.py /usr/local/bin/oracle
```

